import React, { Component } from 'react';
import { Fragment } from 'react'
import "../style.css"



class FeaturedImage extends Component {
    constructor(props){
        super(props);
        this.state = {
            featured_img: ''
        }
    }

    render() {
        return(
            <Fragment>

                {this.props.featured_img  ?
                <img style="width:100%" src={post._embedded['wp:featuredmedia'][0].media_details.sizes["thumbnail"].source_url} /> : null}

            </Fragment>
        );
    }
}
export default FeaturedImage;
