import Navigation from '../components/Navigation'
import Head from '../components/Head'
import PostExcerptCard from '../components/PostExcerptCard'
import React, { Component, Fragment } from 'react'
import axios from 'axios'
import "../style.css"

export default class extends Component {

  // Resolve promise and set initial props.
  static async getInitialProps () {

    // Make request for posts.
    const response = await axios.get( 'http://localhost:8888/codeformylife/wp-json/wp/v2/pages/?slug=about-me')

    // Return response to posts object in props.
    return {
      posts: response.data

    }
  }


  render() {
    return (
      <Fragment>
        <Head />
        <div className="w3-container w3-deep-orange">
          <div className="w3-row">
            <Navigation/>
          </div>
          <div className="w3-row">
            <div className="w3-col">  
              <h1>About me</h1>
            </div>  
          </div>    
          <div className="w3-row">
            {
                  this.props.posts.map( page => {
                    return (
                      <div className="w3-col s12" key={ page.id }>
                        <h3>{ page.title.rendered }</h3>
                        <div dangerouslySetInnerHTML={{__html: page.content.rendered}}></div>
                      </div> 

                    )
                  })
                }


          </div>  
        </div>
      </Fragment>
    )
  }
}
