import React, { Component } from 'react';

export default class Posts2 extends Component {

  constructor(props) {
    super(props);
    this.state = { data: {} };

    this.fetchPostData = this.fetchPostData.bind(this);
    this.renderPosts = this.renderPosts.bind(this);
    this.populatePageAfterFetch = this.populatePageAfterFetch.bind(this);

  }

  componentDidMount() {
    this.fetchPostData();
  }

  fetchPostData() {
    fetch(`http://localhost/codeformylife/wp-json/wp/v2/posts?per_page=100`)
      .then(response => response.json())
      .then(myJSON => {
        let objLength = Object.keys(myJSON).length;
        let newState = this.state;
        console.log("newState", newState)

        for (let i = 0; i < objLength; i++) {
          let objKey = Object.values(myJSON)[i].title.rendered;
          console.log(objKey)
          let currentPost = newState.data[objKey];
          console.log("newState",newState.data[objKey])
          currentPost = {};
          currentPost.name = Object.values(myJSON)[i].title.rendered;
          console.log("curr name",currentPost.name)
          currentPost.excerpt = Object.values(myJSON)[i].excerpt.rendered;
          console.log("curr excerpt",currentPost.excerpt)
          currentPost.featured_image = Object.values(myJSON)[i]['featured_image_url'];
