        }

      this.setState(newState);
      });
  }

  renderPosts() {
    if (this.state.data) {
      const postsArray = Object.values(this.state.data)

      return Object.values(postsArray).map((post, index) => this.populatePageAfterFetch(post, index));
    }
  }

  populatePageAfterFetch(post, index) {
    if (this.state.data) {

      return (
        <div key={index} index={index}> 
          <h2 className="name">{post.title}</h2> 
          <div className="excerpt" dangerouslySetInnerHTML={{__html: post.x}} />
          <img className="featured_image" src={post.featured_image} alt={post.name} />
        </div>
      )
    }
  }

  render() {
      return (
      <>
        <h1>Posts</h1>
        <div>{this.renderPosts()}</div>
      </>
      )
  }
}
